import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import ModalEditarFilme from "../components/ModalEditarFilme";

export default function FilmeDetalhe() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [filme, setFilme] = useState(null);
  const [abrirModal, setAbrirModal] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:5000/filmes/${id}`)
      .then(r => r.json())
      .then(d => setFilme(d));
  }, [id]);

  async function deletarFilme() {
    if (!window.confirm("Deseja realmente excluir este filme?")) return;

    await fetch(`http://localhost:5000/filmes/${id}`, { method: "DELETE" });

    navigate("/");
  }

  async function salvarEdicao(data) {
    const resp = await fetch(`http://localhost:5000/filmes/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data)
    });

    const novoFilme = await resp.json();
    setFilme(novoFilme);
    setAbrirModal(false);
  }

  if (!filme) return <h2>Carregando...</h2>;

  return (
    <div className="detalhes-container">
      <img src={filme.capa} className="detalhe-capa" />

      <div className="detalhes-texto">
        <h1>{filme.titulo}</h1>

        <p><strong>Gêneros:</strong> {filme.generos.join(", ")}</p>
        <p><strong>Ano:</strong> {filme.ano}</p>
        <p><strong>Duração:</strong> {filme.duracao} min</p>
        <p><strong>Diretor:</strong> {filme.diretor}</p>
        <p><strong>Elenco:</strong> {filme.elenco.join(", ")}</p>
        <p><strong>Nota:</strong> {filme.nota}/10</p>
        <p className="descricao">{filme.sinopse}</p>

        <div className="botoes-edicao">
          <button className="btn editar" onClick={() => setAbrirModal(true)}>
            Editar
          </button>

          <button className="btn deletar" onClick={deletarFilme}>
            Excluir
          </button>
        </div>
      </div>

      {abrirModal && (
        <ModalEditarFilme
          filme={filme}
          onClose={() => setAbrirModal(false)}
          onSave={salvarEdicao}
        />
      )}
    </div>
  );
}
