import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";

export default function FilmeDetalhe() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [filme, setFilme] = useState(null);
  const [abrirEditar, setAbrirEditar] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:5000/filmes/${id}`)
      .then(resp => resp.json())
      .then(data => setFilme(data))
      .catch(err => console.log(err));
  }, [id]);

  function deletarFilme() {
    if (!window.confirm("Tem certeza que deseja remover este filme?")) return;

    fetch(`http://localhost:5000/filmes/${id}`, { method: "DELETE" })
      .then(() => {
        alert("Filme removido!");
        navigate("/");
      })
      .catch(() => alert("Erro ao deletar o filme."));
  }

  if (!filme) return <h2>Carregando...</h2>;

  return (
    <div className="detalhes-container">
      
      <img src={filme.capa} className="detalhe-capa" alt={filme.titulo} />

      <div className="detalhes-texto">
        <h1>{filme.titulo}</h1>

        <p><strong>Gêneros:</strong> {filme.generos.join(", ")}</p>
        <p><strong>Ano:</strong> {filme.ano}</p>
        <p><strong>Duração:</strong> {filme.duracao} min</p>
        <p><strong>Diretor:</strong> {filme.diretor}</p>
        <p><strong>Elenco:</strong> {filme.elenco.join(", ")}</p>
        <p><strong>Nota:</strong> {filme.nota}/10</p>

        <p className="descricao">{filme.sinopse}</p>

        {filme.trailer && (
          <a
            className="btn"
            href={filme.trailer}
            target="_blank"
            rel="noreferrer"
          >
            ▶ Assistir Trailer
          </a>
        )}

        {/* BOTÕES DE EDIÇÃO E DELETE */}
        <div className="detalhe-acoes">
          <button className="btn-editar" onClick={() => setAbrirEditar(true)}>
            ✏ Editar Filme
          </button>

          <button className="btn-deletar" onClick={deletarFilme}>
            ❌ Deletar Filme
          </button>
        </div>
      </div>

      {/* Modal de edição (vou montar completo no próximo passo se quiser) */}
      {abrirEditar && (
        <div className="modal-overlay">
          <div className="modal">
            <button className="modal-close" onClick={() => setAbrirEditar(false)}>X</button>
            <h2>Editar Filme</h2>
            <p>Me avise se quer que eu monte o formulário completo aqui.</p>
          </div>
        </div>
      )}
    </div>
  );
}
