import { useEffect, useState } from "react";
import FilmeCard from "../components/FilmeCard";

export default function Home() {
  const [catalogo, setCatalogo] = useState([]);
  const [minhaLista, setMinhaLista] = useState([]);

  // Carregar filmes do cat√°logo
  useEffect(() => {
    fetch("http://localhost:5000/filmes")
      .then(r => r.json())
      .then(data => setCatalogo(data))
      .catch(err => console.log(err));
  }, []);

  // Carregar filmes salvos localmente
  useEffect(() => {
    const listaIds = JSON.parse(localStorage.getItem("lista")) || [];

    async function carregarLista() {
      const filmesSalvos = [];

      for (const id of listaIds) {
        const resp = await fetch(`http://localhost:5000/filmes/${id}`);
        const filme = await resp.json();
        filmesSalvos.push(filme);
      }

      setMinhaLista(filmesSalvos);
    }

    carregarLista();
  }, []);

  return (
    <div className="home-container">

      {/* ---- MINHA LISTA ---- */}
      {minhaLista.length > 0 && (
        <section>
          <h2>üé¨ Minha Lista (‚ÄúQuero ver‚Äù)</h2>

          <div className="catalogo">
            {minhaLista.map(f => (
              <FilmeCard key={f._id} filme={f} />
            ))}
          </div>
        </section>
      )}

      {/* ---- CATALOGO COMPLETO ---- */}
      <section>
        <h2>üìö Cat√°logo Completo</h2>

        <div className="catalogo">
          {catalogo.map(f => (
            <FilmeCard key={f._id} filme={f} />
          ))}
        </div>
      </section>

    </div>
  );
}
