import { useEffect, useState } from "react";
import FilmeCard from "./FilmeCard";

export default function Home() {
  const [filmes, setFilmes] = useState([]);
  const [minhaLista, setMinhaLista] = useState([]);

  // Carregar todos os filmes
  useEffect(() => {
    fetch("http://localhost:5000/filmes")
      .then(resp => resp.json())
      .then(data => setFilmes(data));
  }, []);

  // Fun√ß√£o que atualiza a lista "quero ver"
  function carregarMinhaLista() {
    const ids = JSON.parse(localStorage.getItem("lista")) || [];

    if (ids.length === 0) {
      setMinhaLista([]);
      return;
    }

    Promise.all(
      ids.map(id =>
        fetch(`http://localhost:5000/filmes/${id}`).then(r => r.json())
      )
    ).then(setMinhaLista);
  }

  // Carrega no in√≠cio + escuta altera√ß√µes no storage
  useEffect(() => {
    carregarMinhaLista();

    // Evento personalizado para atualizar imediatamente
    window.addEventListener("storage", carregarMinhaLista);

    return () => window.removeEventListener("storage", carregarMinhaLista);
  }, []);

  return (
    <div className="home">

      {/* MINHA LISTA */}
      <h2>üìå Minha Lista (Quero ver)</h2>
      {minhaLista.length === 0 && <p>Voc√™ n√£o salvou nenhum filme ainda.</p>}

      <div className="lista-container">
        {minhaLista.map(filme => (
          <FilmeCard key={filme._id} filme={filme} />
        ))}
      </div>

      <hr />

      {/* CAT√ÅLOGO COMPLETO */}
      <h2>üìΩÔ∏è Cat√°logo</h2>
      <div className="catalogo-container">
        {filmes.map(filme => (
          <FilmeCard key={filme._id} filme={filme} />
        ))}
      </div>

    </div>
  );
}
