import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import EditarFilmeModal from "../components/EditarFilmeModal";

export default function FilmeDetalhe() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [filme, setFilme] = useState(null);
  const [abrirModal, setAbrirModal] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:5000/filmes/${id}`)
      .then(resp => resp.json())
      .then(data => setFilme(data))
      .catch(err => console.log(err));
  }, [id]);

  function deletarFilme() {
  if (!window.confirm("Tem certeza que deseja remover este filme?")) return;

  fetch(`http://localhost:5000/filmes/${id}`, {
    method: "DELETE"
  })
    .then(() => {
      alert("Filme removido!");
      navigate("/");
    })
    .catch(() => alert("Erro ao deletar o filme"));
}


  if (!filme) return <h2>Carregando...</h2>;

  return (
    <div className="detalhes-container">
      <img src={filme.capa} className="detalhe-capa" alt={filme.titulo} />

      <div className="detalhes-texto">
        <h1>{filme.titulo}</h1>

        <p><strong>G√™neros:</strong> {filme.generos.join(", ")}</p>
        <p><strong>Ano:</strong> {filme.ano}</p>
        <p><strong>Dura√ß√£o:</strong> {filme.duracao} min</p>
        <p><strong>Diretor:</strong> {filme.diretor}</p>
        <p><strong>Elenco:</strong> {filme.elenco.join(", ")}</p>
        <p><strong>Nota:</strong> {filme.nota}/10</p>

        <p className="descricao">{filme.sinopse}</p>

        {filme.trailer && (
          <a
            className="btn"
            href={filme.trailer}
            target="_blank"
            rel="noreferrer"
          >
            ‚ñ∂ Assistir Trailer
          </a>
        )}

        {/* BOT√ïES DE A√á√ÉO */}
        <div style={{ display: "flex", gap: "10px", marginTop: "20px" }}>
          <button className="btn" onClick={() => setAbrirModal(true)}>
            ‚úè Editar
          </button>

          <button className="btn btn-danger" onClick={deletarFilme}>
            üóë Excluir
          </button>
        </div>
      </div>

      {/* MODAL */}
      {abrirModal && (
        <EditarFilmeModal
          filme={filme}
          fechar={() => setAbrirModal(false)}
          atualizarFilme={setFilme}
        />
      )}
    </div>
  );
}
