import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";

export default function FilmeDetalhe() {
  const { id } = useParams();
  const [filme, setFilme] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/filmes/${id}`)
      .then(resp => resp.json())
      .then(data => setFilme(data))
      .catch(err => console.log(err));
  }, []);

  if (!filme) return <h2>Carregando...</h2>;

  return (
    <div className="detalhes-container">
      <img src={filme.capaUrl} className="detalhe-capa" />

      <div className="detalhes-texto">
        <h1>{filme.titulo}</h1>
        <p><strong>Gênero:</strong> {filme.genero}</p>
        <p><strong>Ano:</strong> {filme.ano}</p>
        <p><strong>Diretor:</strong> {filme.diretor}</p>

        <p className="descricao">{filme.descricao}</p>
      </div>
    </div>
  );
}
