import { useEffect, useState } from "react";
import FilmeCard from "../components/FilmeCard";
import ModalNovoFilme from "../components/ModalNovoFilme";
import Header from "../components/Header";

export default function Home() {
  const [catalogo, setCatalogo] = useState([]);
  const [minhaLista, setMinhaLista] = useState([]);
  const [abrirModal, setAbrirModal] = useState(false);

  function carregarCatalogo() {
    fetch("http://localhost:5000/filmes")
      .then(r => r.json())
      .then(data => setCatalogo(data));
  }

  useEffect(() => {
    carregarCatalogo();
  }, []);

  useEffect(() => {
    const listaIds = JSON.parse(localStorage.getItem("lista")) || [];

    async function carregarLista() {
      const filmesSalvos = [];

      for (const id of listaIds) {
        const resp = await fetch(`http://localhost:5000/filmes/${id}`);
        const filme = await resp.json();
        filmesSalvos.push(filme);
      }

      setMinhaLista(filmesSalvos);
    }

    carregarLista();
  }, [catalogo]);

  return (
    <div className="home-container">

      <Header onAddClick={() => setAbrirModal(true)} />

      {abrirModal && (
        <ModalNovoFilme
          onClose={() => setAbrirModal(false)}
          onCadastrado={carregarCatalogo}
        />
      )}

      {/* MINHA LISTA */}
      {minhaLista.length > 0 && (
        <section>
          <h2>Minha Lista</h2>
          <div className="catalogo">
            {minhaLista.map(f => <FilmeCard key={f._id} filme={f} />)}
          </div>
        </section>
      )}

      {/* CATALOGO */}
      <section>
        <h2>Catálogo Completo</h2>
        <div className="catalogo">
          {catalogo.map(f => <FilmeCard key={f._id} filme={f} />)}
        </div>
      </section>
    </div>
  );
}
