import { useEffect, useState } from "react";
import api from "../services/api";
import FilmeCard from "../components/FilmeCard";

export default function MinhaLista() {
  const [filmes, setFilmes] = useState([]);

  useEffect(() => {
    async function carregar() {
      const ids = JSON.parse(localStorage.getItem("lista")) || [];

      if (ids.length === 0) {
        setFilmes([]);
        return;
      }

      const resposta = await api.get("/filmes");
      const listaFiltrada = resposta.data.filter(f => ids.includes(f._id));

      setFilmes(listaFiltrada);
    }
    carregar();
  }, []);

  return (
    <div style={{ padding: "30px" }}>
      <h1>ðŸŽ¥ Minha Lista</h1>

      {filmes.length === 0 && (
        <p>Nenhum filme salvo ðŸ˜¢</p>
      )}

      <div style={{
        display: "flex",
        flexWrap: "wrap",
        gap: "20px"
      }}>
        {filmes.map(f => (
          <FilmeCard key={f._id} filme={f} />
        ))}
      </div>
    </div>
  );
}
