import { useState, useEffect } from "react";

export default function ModalEditarFilme({ filme, onClose, onSave }) {
  const [form, setForm] = useState({
    titulo: "",
    sinopse: "",
    ano: "",
    duracao: "",
    diretor: "",
    nota: "",
    capa: "",
    trailer: "",
    generos: "",
    elenco: ""
  });

  useEffect(() => {
    if (filme) {
      setForm({
        titulo: filme.titulo,
        sinopse: filme.sinopse,
        ano: filme.ano,
        duracao: filme.duracao,
        diretor: filme.diretor,
        nota: filme.nota,
        capa: filme.capa,
        trailer: filme.trailer,
        generos: filme.generos.join(", "),
        elenco: filme.elenco.join(", ")
      });
    }
  }, [filme]);

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function handleSubmit(e) {
    e.preventDefault();

    const data = {
      ...form,
      generos: form.generos.split(",").map(s => s.trim()),
      elenco: form.elenco.split(",").map(s => s.trim())
    };

    onSave(data);
  }

  return (
    <div className="modal-overlay">
      <div className="modal">
        <button className="modal-close" onClick={onClose}>×</button>

        <h2>Editar Filme</h2>

        <form onSubmit={handleSubmit}>
          <input name="titulo" value={form.titulo} onChange={handleChange} placeholder="Título" />
          <input name="ano" value={form.ano} onChange={handleChange} placeholder="Ano" />
          <input name="duracao" value={form.duracao} onChange={handleChange} placeholder="Duração (min)" />
          <input name="diretor" value={form.diretor} onChange={handleChange} placeholder="Diretor" />
          <input name="nota" value={form.nota} onChange={handleChange} placeholder="Nota" />
          <input name="capa" value={form.capa} onChange={handleChange} placeholder="URL da Capa" />
          <input name="trailer" value={form.trailer} onChange={handleChange} placeholder="URL do Trailer" />
          <textarea name="sinopse" value={form.sinopse} onChange={handleChange} placeholder="Sinopse" />

          <input name="generos" value={form.generos} onChange={handleChange} placeholder="Gêneros (separados por vírgula)" />
          <input name="elenco" value={form.elenco} onChange={handleChange} placeholder="Elenco (separado por vírgula)" />

          <button className="btn salvar" type="submit">Salvar alterações</button>
          <button className="btn cancelar" onClick={onClose}>Cancelar</button>
        </form>
      </div>
    </div>
  );
}
