import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import Header from "../components/Header";

export default function FilmeDetalhe() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [filme, setFilme] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);

  const [formData, setFormData] = useState({
    titulo: "",
    ano: "",
    duracao: "",
    diretor: "",
    nota: "",
    capa: "",
    trailer: "",
    sinopse: "",
    generos: "",
    elenco: ""
  });

  useEffect(() => {
    fetch(`http://localhost:5000/filmes/${id}`)
      .then(resp => resp.json())
      .then(data => {
        setFilme(data);

        setFormData({
          titulo: data.titulo,
          ano: data.ano,
          duracao: data.duracao,
          diretor: data.diretor,
          nota: data.nota,
          capa: data.capa,
          trailer: data.trailer,
          sinopse: data.sinopse,
          generos: data.generos.join(", "),
          elenco: data.elenco.join(", ")
        });
      })
      .catch(err => console.log(err));
  }, [id]);

  // Atualiza o form
  function handleChange(e) {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  }

  // Atualizar filme
  function handleUpdate(e) {
    e.preventDefault();

    const atualizado = {
      ...formData,
      generos: formData.generos.split(",").map(s => s.trim()),
      elenco: formData.elenco.split(",").map(s => s.trim())
    };

    fetch(`http://localhost:5000/filmes/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(atualizado)
    })
      .then(() => {
        setFilme(atualizado);
        setShowEditModal(false);
      })
      .catch(err => console.log(err));
  }

  // Deletar filme
  function handleDelete() {
    if (!window.confirm("Tem certeza que deseja excluir este filme?")) return;

    fetch(`http://localhost:5000/filmes/${id}`, {
      method: "DELETE",
    })
      .then(() => navigate("/"))
      .catch(err => console.log(err));
  }

  if (!filme) return <h2>Carregando...</h2>;

  return (
    <>


      <div className="detalhes-container">
        <img src={filme.capa} className="detalhe-capa" alt={filme.titulo} />

        <div className="detalhes-texto">
          <h1>{filme.titulo}</h1>

          <p><strong>G√™neros:</strong> {filme.generos.join(", ")}</p>
          <p><strong>Ano:</strong> {filme.ano}</p>
          <p><strong>Dura√ß√£o:</strong> {filme.duracao} min</p>
          <p><strong>Diretor:</strong> {filme.diretor}</p>
          <p><strong>Elenco:</strong> {filme.elenco.join(", ")}</p>
          <p><strong>Nota:</strong> {filme.nota}/10</p>

          <p className="descricao">{filme.sinopse}</p>

          {filme.trailer && (
            <a
              className="btn"
              href={filme.trailer}
              target="_blank"
              rel="noreferrer"
            >
              ‚ñ∂ Assistir Trailer
            </a>
          )}

          {/* BOT√ïES */}
          <div className="botoes-edicao">
            <button className="btn editar" onClick={() => setShowEditModal(true)}>
              ‚úè Editar
            </button>
            <button className="btn deletar" onClick={handleDelete}>
              üóë Excluir
            </button>
          </div>
        </div>
      </div>

      {/* MODAL DE EDI√á√ÉO */}
      {showEditModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Editar Filme</h2>

            <form onSubmit={handleUpdate}>
              <input name="titulo" value={formData.titulo} onChange={handleChange} placeholder="T√≠tulo" />
              <input name="ano" value={formData.ano} onChange={handleChange} placeholder="Ano" />
              <input name="duracao" value={formData.duracao} onChange={handleChange} placeholder="Dura√ß√£o" />
              <input name="diretor" value={formData.diretor} onChange={handleChange} placeholder="Diretor" />
              <input name="nota" value={formData.nota} onChange={handleChange} placeholder="Nota" />
              <input name="capa" value={formData.capa} onChange={handleChange} placeholder="URL da capa" />
              <input name="trailer" value={formData.trailer} onChange={handleChange} placeholder="URL do trailer" />

              <textarea name="sinopse" value={formData.sinopse} onChange={handleChange} placeholder="Sinopse" />

              <input name="generos" value={formData.generos} onChange={handleChange} placeholder="G√™neros separados por v√≠rgula" />
              <input name="elenco" value={formData.elenco} onChange={handleChange} placeholder="Elenco separado por v√≠rgula" />

              <button type="submit" className="btn salvar">
                Salvar altera√ß√µes
              </button>
              <button type="button" className="btn cancelar" onClick={() => setShowEditModal(false)}>
                Cancelar
              </button>
            </form>
          </div>
        </div>
      )}
    </>
  );
}
