import { Link } from "react-router-dom";
import { addToWatchlist, isInWatchlist } from "../services/watchlist";

export default function FilmeCard({ filme }) {
  
  function handleAdd() {
    addToWatchlist(filme.id);
    alert("Adicionado à lista!");
  }

  return (
    <div style={{ width: "220px" }}>
      <Link to={`/filme/${filme.id}`} style={{ textDecoration: "none", color: "inherit" }}>
        <img 
          src={filme.capaUrl}
          style={{ width: "100%", height: "320px", borderRadius: "10px", objectFit: "cover" }}
        />
      </Link>

      <h3>{filme.titulo}</h3>

      <button 
        disabled={isInWatchlist(filme.id)}
        onClick={handleAdd}
        style={{
          padding: "8px 12px",
          marginTop: "5px",
          background: isInWatchlist(filme.id) ? "#666" : "#222",
          color: "white",
          border: "none",
          cursor: "pointer"
        }}
      >
        {isInWatchlist(filme.id) ? "Na Lista ✔" : "Adicionar à Lista"}
      </button>
    </div>
  );
}
