import { useEffect, useState } from "react";

export default function FilmeCard({ filme }) {
  const [salvo, setSalvo] = useState(false);

 
  useEffect(() => {
    const lista = JSON.parse(localStorage.getItem("lista")) || [];
    setSalvo(lista.includes(filme._id));
  }, []);

  function toggleSalvar() {
    const lista = JSON.parse(localStorage.getItem("lista")) || [];

    if (lista.includes(filme._id)) {
 
      const novaLista = lista.filter(id => id !== filme._id);
      localStorage.setItem("lista", JSON.stringify(novaLista));
      setSalvo(false);
    } else {
     
      lista.push(filme._id);
      localStorage.setItem("lista", JSON.stringify(lista));
      setSalvo(true);
    }
  }

  return (
    <div style={{
      border: "1px solid #ccc",
      borderRadius: "8px",
      padding: "16px",
      width: "200px"
    }}>
      <img 
        src={filme.capaUrl} 
        alt={filme.titulo}
        style={{ width: "100%", borderRadius: "8px" }}
      />
      <h3>{filme.titulo}</h3>
      <p>{filme.genero} • {filme.ano}</p>

      <button 
        onClick={toggleSalvar}
        style={{
          marginTop: "10px",
          padding: "8px 12px",
          background: salvo ? "green" : "#333",
          color: "white",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer"
        }}
      >
        {salvo ? "Salvo ✔" : "Quero ver"}
      </button>
    </div>
  );
}
