export default function FilmeCard({ filme }) {
  const [salvo, setSalvo] = useState(false);

  useEffect(() => {
    const lista = JSON.parse(localStorage.getItem("lista")) || [];
    setSalvo(lista.includes(filme._id));
  }, []);

  function toggleSalvar() {
    const lista = JSON.parse(localStorage.getItem("lista")) || [];

    if (lista.includes(filme._id)) {
      localStorage.setItem("lista", JSON.stringify(lista.filter(id => id !== filme._id)));
      setSalvo(false);
    } else {
      lista.push(filme._id);
      localStorage.setItem("lista", JSON.stringify(lista));
      setSalvo(true);
    }
  }

  return (
    <div style={{
      background: "#fff",
      borderRadius: "12px",
      boxShadow: "0 4px 15px rgba(0,0,0,0.2)",
      width: "220px",
      overflow: "hidden"
    }}>
      <img 
        src={filme.capaUrl}
        style={{ width: "100%", height: "320px", objectFit: "cover" }}
      />

      <div style={{ padding: "15px" }}>
        <h3 style={{ margin: "0 0 10px" }}>{filme.titulo}</h3>
        <p style={{ color: "#555" }}>{filme.genero} • {filme.ano}</p>

        <button 
          onClick={toggleSalvar}
          style={{
            width: "100%",
            marginTop: "10px",
            padding: "10px",
            background: salvo ? "#2ecc71" : "#1c1c1c",
            color: "white",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer"
          }}
        >
          {salvo ? "Salvo ✔" : "Quero ver"}
        </button>
      </div>
    </div>
  );
}
