import { useState } from "react";

export default function ModalNovoFilme({ onClose, onCadastrado }) {
  const [form, setForm] = useState({
    titulo: "",
    sinopse: "",
    ano: "",
    duracao: "",
    generos: "",
    diretor: "",
    elenco: "",
    nota: "",
    capa: "",
    trailer: "",
    destaque: false
  });

  function atualizar(e) {
    const { name, value, type, checked } = e.target;
    setForm({ ...form, [name]: type === "checkbox" ? checked : value });
  }

  function enviar(e) {
    e.preventDefault();

    const payload = {
      titulo: form.titulo,
      sinopse: form.sinopse,
      ano: Number(form.ano),
      duracao: Number(form.duracao),
      generos: form.generos.split(",").map(g => g.trim()),
      diretor: form.diretor,
      elenco: form.elenco.split(",").map(a => a.trim()),
      nota: Number(form.nota),
      capa: form.capa,
      trailer: form.trailer,
      destaque: form.destaque
    };

    fetch("http://localhost:5000/filmes", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload)
    })
      .then(r => r.json())
      .then(() => {
        alert("Filme cadastrado!");
        onClose();
        onCadastrado(); // recarregar lista
      })
      .catch(err => {
        console.error(err);
        alert("Erro ao cadastrar.");
      });
  }

  return (
    <div className="modal-fundo">
      <div className="modal">
        <h2>Novo Filme</h2>

        <form onSubmit={enviar} className="form">

          <input name="titulo" placeholder="Título" value={form.titulo} onChange={atualizar} required />
          <input name="sinopse" placeholder="Sinopse" value={form.sinopse} onChange={atualizar} required />
          <input name="ano" type="number" placeholder="Ano" value={form.ano} onChange={atualizar} required />
          <input name="duracao" type="number" placeholder="Duração" value={form.duracao} onChange={atualizar} required />
          <input name="generos" placeholder="Gêneros (vírgula)" value={form.generos} onChange={atualizar} required />
          <input name="diretor" placeholder="Diretor" value={form.diretor} onChange={atualizar} required />
          <input name="elenco" placeholder="Elenco (vírgula)" value={form.elenco} onChange={atualizar} required />
          <input name="nota" type="number" placeholder="Nota" value={form.nota} onChange={atualizar} required />
          <input name="capa" placeholder="URL da capa" value={form.capa} onChange={atualizar} required />
          <input name="trailer" placeholder="URL do Trailer" value={form.trailer} onChange={atualizar} required />

          <label>
            <input type="checkbox" name="destaque" checked={form.destaque} onChange={atualizar} />
            Destaque?
          </label>

          <button className="btn">Cadastrar</button>
        </form>

        <button className="btn-cancelar" onClick={onClose}>Cancelar</button>
      </div>
    </div>
  );
}
