import { useState } from "react";

export default function AdicionarFilme() {
  const [form, setForm] = useState({
    titulo: "",
    descricao: "",
    genero: "",
    ano: "",
    diretor: "",
    capaUrl: ""
  });

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function handleSubmit(e) {
    e.preventDefault();

    const resposta = await fetch("http://localhost:5000/filmes", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form)
    });

    if (resposta.ok) {
      alert("Filme cadastrado com sucesso!");
      setForm({
        titulo: "",
        descricao: "",
        genero: "",
        ano: "",
        diretor: "",
        capaUrl: ""
      });
    } else {
      alert("Erro ao cadastrar!");
    }
  }

  return (
    <div style={{ padding: "40px", maxWidth: "500px", margin: "auto" }}>
      <h1>Adicionar Filme</h1>

      <form onSubmit={handleSubmit} style={{ display: "grid", gap: "12px" }}>
        <input
          type="text"
          name="titulo"
          placeholder="Título"
          value={form.titulo}
          onChange={handleChange}
          required
        />

        <input
          type="text"
          name="descricao"
          placeholder="Descrição"
          value={form.descricao}
          onChange={handleChange}
          required
        />

        <input
          type="text"
          name="genero"
          placeholder="Gênero"
          value={form.genero}
          onChange={handleChange}
        />

        <input
          type="number"
          name="ano"
          placeholder="Ano"
          value={form.ano}
          onChange={handleChange}
        />

        <input
          type="text"
          name="diretor"
          placeholder="Diretor"
          value={form.diretor}
          onChange={handleChange}
        />

        <input
          type="text"
          name="capaUrl"
          placeholder="URL da Capa"
          value={form.capaUrl}
          onChange={handleChange}
          required
        />

        <button type="submit" style={{ padding: "10px", background: "#111", color: "white" }}>
          Salvar
        </button>
      </form>
    </div>
  );
}
