import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import { addToWatchlist, isInWatchlist } from "../services/watchlist";

export default function PaginaFilme() {
  const { id } = useParams();
  const [filme, setFilme] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/filmes/${id}`)
      .then(res => res.json())
      .then(data => setFilme(data))
      .catch(() => alert("Erro ao carregar filme"));
  }, [id]);

  if (!filme) return <h2>Carregando...</h2>;

  return (
    <div style={{ padding: "20px" }}>
      <img src={filme.capaUrl} style={{ width: "300px", borderRadius: "10px" }} />

      <h1>{filme.titulo}</h1>
      <p>{filme.descricao}</p>

      <button
        disabled={isInWatchlist(filme.id)}
        onClick={() => addToWatchlist(filme.id)}
        style={{
          padding: "10px 20px",
          marginTop: "10px",
          background: isInWatchlist(filme.id) ? "#666" : "#222",
          color: "white",
          border: "none",
          cursor: "pointer"
        }}
      >
        {isInWatchlist(filme.id) ? "Na Lista ✔" : "Adicionar à Lista"}
      </button>
    </div>
  );
}
