import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function NovoFilme() {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    titulo: "",
    sinopse: "",
    ano: "",
    duracao: "",
    generos: "",
    diretor: "",
    elenco: "",
    nota: "",
    capa: "",
    trailer: "",
    destaque: false
  });

  function atualizar(e) {
    const { name, value, type, checked } = e.target;
    setForm({
      ...form,
      [name]: type === "checkbox" ? checked : value
    });
  }

  function enviar(e) {
    e.preventDefault();

    const payload = {
      titulo: form.titulo,
      sinopse: form.sinopse,
      ano: Number(form.ano),
      duracao: Number(form.duracao),
      generos: form.generos.split(",").map(g => g.trim()),
      diretor: form.diretor,
      elenco: form.elenco.split(",").map(a => a.trim()),
      nota: Number(form.nota),
      capa: form.capa,
      trailer: form.trailer,
      destaque: form.destaque
    };

    fetch("http://localhost:5000/filmes", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload)
    })
      .then(resp => {
        if (!resp.ok) throw new Error("Erro ao cadastrar");
        return resp.json();
      })
      .then(() => {
        alert("Filme cadastrado!");
        navigate("/");
      })
      .catch(err => {
        console.error(err);
        alert("Erro ao enviar os dados.");
      });
  }

  return (
    <div className="form-container">
      <h1>Novo Filme</h1>

      <form onSubmit={enviar} className="form">

        <input name="titulo" placeholder="Título" value={form.titulo} onChange={atualizar} required />
        <input name="sinopse" placeholder="Sinopse" value={form.sinopse} onChange={atualizar} required />
        <input name="ano" placeholder="Ano" type="number" value={form.ano} onChange={atualizar} required />
        <input name="duracao" placeholder="Duração (minutos)" type="number" value={form.duracao} onChange={atualizar} required />
        <input name="generos" placeholder="Gêneros (separados por vírgula)" value={form.generos} onChange={atualizar} required />
        <input name="diretor" placeholder="Diretor" value={form.diretor} onChange={atualizar} required />
        <input name="elenco" placeholder="Elenco (separado por vírgula)" value={form.elenco} onChange={atualizar} required />
        <input name="nota" placeholder="Nota (0 a 10)" type="number" value={form.nota} onChange={atualizar} required />
        <input name="capa" placeholder="URL da capa" value={form.capa} onChange={atualizar} required />
        <input name="trailer" placeholder="URL do trailer" value={form.trailer} onChange={atualizar} required />

        <label style={{ display: "flex", gap: "10px", alignItems: "center" }}>
          <input type="checkbox" name="destaque" checked={form.destaque} onChange={atualizar} />
          Destaque?
        </label>

        <button className="btn">Cadastrar</button>
      </form>
    </div>
  );
}
