import { useState } from "react";
import api from "../services/api";

export default function AdicionarFilme() {
  const [titulo, setTitulo] = useState("");
  const [descricao, setDescricao] = useState("");
  const [genero, setGenero] = useState("");
  const [ano, setAno] = useState("");
  const [capaUrl, setCapaUrl] = useState("");

  async function enviar(e) {
    e.preventDefault();

    try {
      await api.post("/filmes", {
        titulo,
        descricao,
        genero,
        ano,
        capaUrl
      });

      alert("Filme adicionado com sucesso!");
      setTitulo("");
      setDescricao("");
      setGenero("");
      setAno("");
      setCapaUrl("");
    } catch (err) {
      alert("Erro ao adicionar filme.");
    }
  }

  return (
    <div style={{ padding: "30px" }}>
      <h1>Adicionar Filme</h1>

      <form onSubmit={enviar} style={{
        display: "flex",
        flexDirection: "column",
        maxWidth: "400px",
        gap: "15px"
      }}>
        <input placeholder="Título" value={titulo} onChange={e => setTitulo(e.target.value)} />
        <input placeholder="Descrição" value={descricao} onChange={e => setDescricao(e.target.value)} />
        <input placeholder="Gênero" value={genero} onChange={e => setGenero(e.target.value)} />
        <input placeholder="Ano" value={ano} onChange={e => setAno(e.target.value)} />
        <input placeholder="URL da capa" value={capaUrl} onChange={e => setCapaUrl(e.target.value)} />

        <button style={{
          padding: "10px",
          background: "#1c1c1c",
          color: "white",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
          fontSize: "16px"
        }}>Adicionar</button>
      </form>
    </div>
  );
}
